import { CustomFunctions } from 'src/app/shared/customFunctions/customFunctions';
import { CustomService } from 'src/app/service/custom.service';
import { Observable } from 'rxjs';
import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators } from "@angular/forms";
import { ActivatedRoute, Router } from "@angular/router";
import { Location } from '@angular/common';
declare var $: any;

@Component({
  selector: 'app-rating-offer',
  templateUrl: './ratingOffer.component.html',
  styleUrls: ['./ratingOffer.component.scss']
})
export class RatingOfferComponent implements OnInit {
  projectOfferRatingForm!: FormGroup;
  projectOffer$!: Observable<any>;
  projectOfferId!: string | null;
  submitted = false;
  constructor(private router: Router,
    private route: ActivatedRoute,
    private _location: Location,
    private customFunctions: CustomFunctions,
    private customService: CustomService,
    private formBuilder: FormBuilder) { }

  ngOnInit() {
    this.projectOfferRatingForm = this.formBuilder.group({
      Rate: ['', Validators.required],
      description: ['', Validators.required],
    });
    this.route.paramMap.subscribe(param => {
      if (param.has('offerId')) {
        this.projectOfferId = param.get('offerId');
        this.projectOffer$ = this.customService.Get(`ProjectOffer/${param.get('offerId')}`);
      }
    });
  }
  get form() {
    return this.projectOfferRatingForm.controls;
  }
  onSubmit() {
    this.submitted = true;
    if (this.projectOfferRatingForm.invalid) {
      this.customFunctions.showResponseMessage('Please validate All Filed is Correct ', false);
      return;
    }
    const projectOfferRating: any = {
      projectOwnerMessage: this.projectOfferRatingForm.controls.description.value,
      rate: this.projectOfferRatingForm.controls.Rate.value,
      offerId: this.projectOfferId
    };
    this.customService.post(projectOfferRating, 'ProjectOfferRating').subscribe(res => {
      if (res) {
        this.customFunctions.showResponseMessage(res.description, res.code);
        if (res.code <= 300) {
          this._location.back();
        }
      }
    });
  }

  hide() {
    this.projectOfferRatingForm.reset();
  }


}
<section class="pt-90" *ngIf="projectOffer$ | async as projectOffer">
  <div class="container">
    <div class="row">
      <div class="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
        <div class="card find-project">
          <div class="card-body">
            <div class="search-form prjct-form">
              <h4>{{projectOffer?.projectName}}</h4>
              <p>{{projectOffer?.userName}}</p>

              <div class="row">
                <div class="col-md-4">
                  <h5>{{'BASE.DATE' | translate}} <span
                      class="badge badge-secondary">{{projectOffer?.createdDate | date : 'medium'}}</span></h5>
                </div>
                <div class="col-md-4">
                  <h5>{{'PROJECT_BIDS.BID_PRICE' | translate}} <span
                      class="badge badge-secondary">${{projectOffer?.price}}</span></h5>
                </div>
              </div>
            </div>
            <form class="form-s" [formGroup]="projectOfferRatingForm">
              <div class="row">
                <div class="col-md-12">
                  <ngx-star-rating formControlName="Rate" [id]="'rating'"></ngx-star-rating>
                </div>
              </div>
              <div class="row">
                <div class="col-md-6">
                  <div class="form-group">
                    <label for=""> {{'BASE.NOTES' | translate}}</label>
                    <textarea id="description" formControlName="description" cols="70" rows="6"
                      [ngClass]="{'is-invalid': submitted && projectOfferRatingForm.controls.description.errors}"
                      placeholder=" {{'BASE.NOTES' | translate}}"></textarea>
                  </div>
                  <div *ngIf="submitted && form.description.errors" class="invalid-feedback">
                    <span *ngIf="form.description.errors">{{'BASE.IS_REQUIRED' | translate}}</span>
                  </div>
                </div>
              </div>
              <div class="row">
                <div class="col-md-8">
                  <button type="button" (click)="onSubmit()"
                    class="btn btn-success  btn-block">{{'BASE.SUBMIT' | translate}}</button>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  </div>


</section>
<app-project-footer></app-project-footer>
