    i0.ɵɵtext(109, " \u0623\u0633\u0626\u0644\u0629 \u0627\u0644\u0628\u062D\u062B \u0648\u0623\u0647\u062F\u0627\u0641\u0647: \u062A\u062D\u062F\u064A\u062F \u0627\u0644\u0623\u0633\u0626\u0644\u0629 \u0627\u0644\u0639\u0644\u0645\u064A\u0629 \u0627\u0644\u062A\u064A \u064A\u0645\u0643\u0646 \u0627\u0644\u0625\u062C\u0627\u0628\u0629 \u0639\u0644\u064A\u0647\u0627 \u0641\u064A \u0627\u0644\u0628\u062D\u062B \u0623\u0648 \u0627\u0644\u0625\u0634\u0627\u0631\u0629 \u0625\u0644\u0649 \u0623\u0647\u062F\u0627\u0641 \u0627\u0644\u0628\u062D\u062B. \u0645\u0631\u0627\u062C\u0639\u0629 \u0627\u0644\u0623\u062F\u0628\u064A\u0627\u062A: \u0627\u0644\u0648\u0635\u0648\u0644 \u0625\u0644\u0649 \u0642\u0627\u0639\u062F\u0629 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A \u0627\u0644\u0635\u062D\u064A\u062D\u0629 \u0648\u0627\u0644\u0639\u0645\u0644 \u0639\u0644\u0649 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A \u0648\u062A\u062D\u0644\u064A\u0644\u0647\u0627. \u0645\u0631\u0627\u062C\u0639\u0629 \u0627\u0644\u062A\u0635\u0648\u0631\u0627\u062A: \u0627\u0644\u0645\u0633\u0627\u0639\u062F\u0629 \u0641\u064A \u062A\u0642\u062F\u064A\u0645 \u0645\u0646\u062D\u0629 \u0623\u0648 \u0628\u062D\u062B \u0623\u0648 \u062A\u0635\u0648\u0631 \u062A\u0645\u0648\u064A\u0644 \u0628\u0646\u062C\u0627\u062D. \u0645\u0631\u0627\u062C\u0639\u0629 \u0627\u0644\u0623\u0648\u0631\u0627\u0642 \u0627\u0644\u0639\u0644\u0645\u064A\u0629: \u0645\u0631\u0627\u062C\u0639\u0629 \u0627\u0644\u0623\u0648\u0631\u0627\u0642 \u0627\u0644\u0639\u0644\u0645\u064A\u0629 \u0648\u0627\u0644\u062A\u062D\u0642\u0642 \u0645\u0627 \u0625\u0630\u0627 \u0643\u0627\u0646\u062A \u062C\u0627\u0647\u0632\u0629 \u0644\u0644\u0646\u0634\u0631 \u0623\u0645 \u0644\u0627. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(110, "li");
    i0.ɵɵtext(111, "\u0623\u062F\u0648\u0627\u062A \u0628\u0631\u0645\u062C\u0629 \u0648\u062A\u0635\u0645\u064A\u0645 \u0627\u0644\u0628\u062D\u062B (\u0628\u0646\u0627\u0621 \u0642\u0627\u0639\u062F\u0629 \u0628\u064A\u0627\u0646\u0627\u062A \u060C \u062A\u0635\u0645\u064A\u0645 \u0628\u0648\u0633\u062A\u0631).");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(112, "li");
    i0.ɵɵelementStart(113, "h6");
    i0.ɵɵtext(114, " \u062E\u062F\u0645\u0627\u062A \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A:-");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(115, "ol");
    i0.ɵɵelementStart(116, "li");
    i0.ɵɵtext(117, " \u062C\u0645\u0639 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A: \u062A\u0648\u062C\u064A\u0647 \u0648\u0645\u0631\u0627\u062C\u0639\u0629 \u0627\u0644\u0625\u062C\u0631\u0627\u0621\u0627\u062A \u0627\u0644\u0645\u062A\u0628\u0639\u0629 \u0641\u064A \u0639\u0645\u0644\u064A\u0629 \u062C\u0645\u0639 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(118, "li");
    i0.ɵɵtext(119, "\u062A\u062D\u0644\u064A\u0644 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A: \u062A\u0637\u0628\u064A\u0642 \u0627\u0644\u062A\u0642\u0646\u064A\u0627\u062A \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645\u0629 \u0641\u064A \u062A\u062D\u0644\u064A\u0644 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(120, "li");
    i0.ɵɵtext(121, "\u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A \u0627\u0644\u0645\u0641\u0642\u0648\u062F\u0629: \u0627\u0644\u0642\u062F\u0631\u0629 \u0639\u0644\u0649 \u0645\u0639\u0627\u0644\u062C\u0629 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A \u0627\u0644\u0645\u0641\u0642\u0648\u062F\u0629 \u0628\u0637\u0631\u064A\u0642\u062A\u064A\u0646\u060C \u0648\u0647\u0645\u0627: \u0627\u062D\u062A\u0633\u0627\u0628 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A \u0648\u062D\u0630\u0641 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(122, "li");
    i0.ɵɵtext(123, "\u0627\u0644\u062A\u062D\u0642\u0642 \u0645\u0646 \u0635\u0650\u062D\u0629 \u0648\u0645\u0648\u062B\u0648\u0642\u064A\u0629 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A: \u062A\u0642\u064A\u064A\u0645 \u062C\u0648\u062F\u0629 \u0627\u0644\u0628\u062D\u062B \u0628\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0623\u062F\u0648\u0627\u062A \u062A\u062D\u0642\u0642 \u0635\u0650\u062D\u0629 \u0623\u0648 \u0645\u0648\u062B\u0648\u0642\u064A\u0629 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(124, "li");
    i0.ɵɵtext(125, "\u0627\u0644\u062C\u062F\u0627\u0648\u0644 \u0648\u0627\u0644\u0631\u0633\u0648\u0645 \u0627\u0644\u0628\u064A\u0627\u0646\u064A\u0629: \u062A\u0635\u0648\u064A\u0631 \u0627\u0644\u0627\u0633\u062A\u0646\u062A\u0627\u062C\u0627\u062A \u0628\u064A\u0627\u0646\u064A\u0651\u064B\u0627 \u0628\u0637\u0631\u064A\u0642\u0629 \u062A\u062A\u0648\u0627\u0641\u0642 \u0645\u0639 \u0627\u0644\u0645\u062A\u0637\u0644\u0628\u0627\u062A \u0627\u0644\u062A\u064A \u062A\u062D\u062F\u062F\u0647\u0627 \u0627\u0644\u0648\u0631\u0642\u0629 \u0627\u0644\u0639\u0644\u0645\u064A\u0629 \u0627\u0644\u062A\u064A \u064A\u0631\u064A\u062F \u0627\u0644\u0639\u0645\u064A\u0644 \u0646\u0634\u0631\u0647\u0627.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(126, "li");
    i0.ɵɵtext(127, "\u062A\u0641\u0633\u064A\u0631 \u0627\u0644\u0631\u0633\u0648\u0645 \u0627\u0644\u0628\u064A\u0627\u0646\u064A\u0629: \u0634\u0631\u062D \u0648\u0641\u0647\u0645 \u0645\u0627 \u062A\u0639\u0631\u0636\u0647 \u0627\u0644\u0631\u0633\u0648\u0645\u0627\u062A \u0648\u0627\u0644\u062C\u062F\u0627\u0648\u0644 \u0627\u0644\u0628\u064A\u0627\u0646\u064A\u0629 \u0648\u062A\u062D\u062F\u064A\u062F \u0646\u062A\u0627\u0626\u062C \u062A\u062D\u0644\u064A\u0644 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(128, "li");
    i0.ɵɵtext(129, "\u0643\u062A\u0627\u0628\u0629 \u0627\u0644\u062A\u0642\u0627\u0631\u064A\u0631 \u0627\u0644\u0625\u062D\u0635\u0627\u0626\u064A\u0629");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(130, "li");
    i0.ɵɵtext(131, "\u0645\u0631\u0627\u062C\u0639\u0629 \u0627\u0644\u062A\u0642\u0627\u0631\u064A\u0631 \u0623\u0648 \u0627\u0644\u062C\u0632\u0626\u064A\u0627\u062A \u0627\u0644\u0625\u062D\u0635\u0627\u0626\u064A\u0629: \u0645\u0631\u0627\u062C\u0639\u0629 \u0627\u0644\u062C\u0632\u0621 \u0627\u0644\u0625\u062D\u0635\u0627\u0626\u064A \u0645\u0646 \u0627\u0644\u0628\u062D\u062B \u0648\u0627\u0644\u062A\u0623\u0643\u062F \u0645\u0646 \u0627\u0644\u062A\u0642\u0631\u064A\u0631 \u0642\u0628\u0644 \u0646\u0634\u0631\u0647.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementEnd();
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(132, "li");
    i0.ɵɵelementStart(133, "h6");
    i0.ɵɵtext(134, "\u062E\u062F\u0645\u0627\u062A \u0627\u0644\u062A\u0635\u0645\u064A\u0645:-");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(135, "ol");
    i0.ɵɵelementStart(136, "li");
    i0.ɵɵtext(137, "\u062A\u0635\u0645\u064A\u0645 \u0627\u0644\u0639\u0631\u0648\u0636 \u0627\u0644\u062A\u0642\u062F\u064A\u0645\u064A\u0629");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(138, "li");
    i0.ɵɵtext(139, "\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0639\u062F\u0629 \u0628\u0631\u0627\u0645\u062C \u0644\u0644\u062A\u0635\u0645\u064A\u0645\u060C \u0645\u062B\u0644: \u0628\u0631\u0627\u0645\u062C \u0645\u0627\u064A\u0643\u0631\u0648\u0633\u0648\u0641\u062A \u0644\u062A\u0635\u0645\u064A\u0645 \u0627\u0644\u0639\u0631\u0636 \u0627\u0644\u062A\u0642\u062F\u064A\u0645\u064A \u0648\u0627\u0644\u0623\u0648\u0631\u0627\u0642 \u0627\u0644\u0628\u062D\u062B\u064A\u0629");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(140, "li");
    i0.ɵɵtext(141, "\u062A\u0635\u0645\u064A\u0645 \u0627\u0644\u0623\u0648\u0631\u0627\u0642 \u0627\u0644\u0628\u062D\u062B\u064A\u0629");
