    i0.ɵɵelementStart(143, "h6");
    i0.ɵɵtext(144, "\u062E\u062F\u0645\u0627\u062A \u0627\u0644\u0628\u0631\u0645\u062C\u0629:-");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(145, "ol");
    i0.ɵɵelementStart(146, "li");
    i0.ɵɵtext(147, "\u0637\u0644\u0628 \u0644\u062C\u0645\u0639 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(148, "li");
    i0.ɵɵtext(149, "\u0627\u0644\u0642\u062F\u0631\u0629 \u0639\u0644\u0649 \u062A\u0635\u0645\u064A\u0645 \u062A\u0637\u0628\u064A\u0642\u0627\u062A \u0644\u0640\u0640 : ");
    i0.ɵɵelementStart(150, "ol");
    i0.ɵɵelementStart(151, "li");
    i0.ɵɵtext(152, "\u0637\u0644\u0628 \u0644\u062A\u0645\u062B\u064A\u0644 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A");
