    i0.ɵɵelementStart(153, "li");
    i0.ɵɵtext(154, "\u0639\u0644\u0648\u0645 \u0627\u0644\u0628\u064A\u0627\u0646\u0627\u062A: \u0627\u0644\u0642\u062F\u0631\u0629 \u0639\u0644\u0649 \u0627\u0644\u062A\u0639\u0627\u0645\u0644 \u0645\u0639 \u0623\u0646\u0648\u0627\u0639 \u0645\u062E\u062A\u0644\u0641\u0629 \u0645\u0646 \u0627\u0644\u0628\u0631\u0627\u0645\u062C \u0627\u0644\u0625\u062D\u0635\u0627\u0626\u064A\u0629.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(155, "li");
    i0.ɵɵtext(156, "\u0644\u0648\u062D\u0629 \u0627\u0644\u0642\u064A\u0627\u062F\u0629: \u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0623\u062F\u0627\u0629 \u0625\u062F\u0627\u0631\u0629 \u0627\u0644\u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0627\u0644\u062A\u064A \u062A\u062A\u062A\u0628\u0639 \u0628\u0635\u0631\u064A\u064B\u0627 \u0648\u062A\u0642\u064A\u0651\u0645 \u0648\u062A\u0639\u0631\u0636 \u0645\u0624\u0634\u0631\u0627\u062A \u0627\u0644\u0623\u062F\u0627\u0621 \u0627\u0644\u0631\u0626\u064A\u0633\u064A\u0629 \u062C\u0646\u0628\u064B\u0627 \u0625\u0644\u0649 \u062C\u0646\u0628 \u0645\u0639 \u0627\u0644\u0645\u0642\u0627\u064A\u064A\u0633.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementEnd();
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(157, "li");
    i0.ɵɵelementStart(158, "h6");
