    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(160, "ol");
    i0.ɵɵelementStart(161, "li");
    i0.ɵɵtext(162, "\u062A\u0631\u062C\u0645\u0629 \u0627\u0644\u0645\u0627\u062F\u0629 \u0627\u0644\u0645\u0637\u0628\u0648\u0639\u0629: \u062A\u0623\u0643\u064A\u062F \u0648\u062A\u0648\u062C\u064A\u0647 \u0627\u0644\u0645\u0627\u062F\u0629 \u0627\u0644\u0645\u0637\u0628\u0648\u0639\u0629 \u0625\u0630\u0627 \u0643\u0627\u0646\u062A \u062A\u062A\u0646\u0627\u0633\u0628 \u0645\u0639 \u0627\u0644\u0645\u0639\u064A\u0627\u0631 \u0627\u0644\u0645\u0637\u0644\u0648\u0628 \u0644\u0644\u0646\u0634\u0631\u060C \u0648\u062A\u062D\u0633\u064A\u0646 \u062C\u0648\u062F\u062A\u0647\u0627.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(163, "li");
    i0.ɵɵtext(164, "\u062A\u0631\u062C\u0645\u0629 \u0627\u0644\u062A\u0635\u0648\u0631\u0627\u062A: \u0627\u0644\u0642\u062F\u0631\u0629 \u0639\u0644\u0649 \u062A\u0631\u062C\u0645\u0629 \u0627\u0644\u062A\u0635\u0648\u0631\u0627\u062A \u0644\u0643\u0644\u0651\u064D \u0645\u0646 \u0627\u0644\u0623\u0648\u0631\u0627\u0642 \u0627\u0644\u0628\u062D\u062B\u064A\u0629 \u0648\u0627\u0644\u0645\u0634\u0631\u0648\u0639.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(165, "li");
    i0.ɵɵtext(166, "\u0627\u0644\u062A\u062F\u0642\u064A\u0642 \u0627\u0644\u0644\u063A\u0648\u064A: \u0642\u0631\u0627\u0621\u0629 \u0627\u0644\u0646\u0633\u062E\u0629 \u0625\u0644\u0643\u062A\u0631\u0648\u0646\u064A\u0629 \u0645\u0646 \u0645\u0646\u0634\u0648\u0631 \u0644\u0625\u064A\u062C\u0627\u062F \u0648\u062A\u0635\u062D\u064A\u062D \u0627\u0644\u0623\u062E\u0637\u0627\u0621 \u0627\u0644\u0644\u063A\u0648\u064A\u0629 \u0641\u064A \u0646\u0635 \u0627\u0644\u0623\u0648\u0631\u0627\u0642 \u0627\u0644\u0628\u062D\u062B\u064A\u0629.");
