import { CustomFunctions } from 'src/app/shared/customFunctions/customFunctions';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs/internal/Observable';
import { CustomService } from 'src/app/service/custom.service';
import * as moment from 'moment';
// import moment, * as moments from "moment-timezone";
import { Moment } from "moment-timezone";
@Component({
  selector: 'app-notifications',
  templateUrl: './notifications.component.html',
  styleUrls: ['./notifications.component.scss']
})
export class NotificationsComponent implements OnInit {

  notifications$!: Observable<any>;
  constructor(private customService: CustomService, private router: ActivatedRoute, public customFunctions: CustomFunctions) { }
  // _moment = momentz;
  ngOnInit(): void {
    this.customService.Get(`Identity/ReadReceiverMessages`).subscribe(ReadReceiverMessages => {
      console.log('ReadReceiverMessages ', ReadReceiverMessages);

    });
    // let tzOfTZ = momentz().tz("Europe/London").format('Z');
    moment.locale('en'); // set to english
    console.log('moment.locale() ', moment.locale());
    this.notifications$ = this.customService.Get(`Identity/GetNotifications`);
    this.readReceiverMessages();
  }
  readReceiverMessages(): void {

  }
  getNotificationUrl(notification: any): any {
    switch (notification.subject) {
      case 'NewBide':
        return `/projects/dashboard`;
      case 'NewInviteBide':
        return `/projects/dashboard`;
      case 'NominationExpert':
        return `/projects/dashboard`;
      case 'AcceptedExpert':
        return `/projects/dashboard`;
      case 'CompletedBYProjectOwner':
        return `/projects/dashboard`;
      case 'CompletedByExpert':
        return `/projects/dashboard`;
      default:
        return `/projects/dashboard`;
    }


  }
}
<section class="section-50">
  <div class="container">
    <h3 class="m-b-50 heading-line" *ngIf="!customFunctions.IsRTL()">{{'BASE.NOTIFICATION' | translate}} <i class="fa fa-bell text-muted"></i></h3>
	<h3 class="m-b-50 heading-line-ar" *ngIf="customFunctions.IsRTL()" style="text-align: right;"><i class="fa fa-bell text-muted"></i>{{'BASE.NOTIFICATION' | translate}} </h3>
    <div class="notification-ui_dd-content">
      <div class="notification-list notification-list--read" *ngFor="let notification of notifications$ | async"
        [routerLink]="getNotificationUrl(notification)">
        <div class="notification-list_content">
          <div class="notification-list_img"> <img src="{{notification.senderImage}}" alt="user"> </div>
          <div class="notification-list_detail">
            <p><b>{{notification.senderName}}</b> : {{notification.subject}}</p>
            <p class="text-muted">{{notification.message}}.</p>
            <p class="text-muted"><small>{{notification.createdAt | amTimeAgo}}</small></p>
          </div>
        </div>
        <!-- <div class="notification-list_feature-img"> <img src="images/features/random1.jpg" alt="Feature image"> </div> -->
      </div>

    </div>
    <div class="text-center" style="display:none;"> <a class="btn btn-success" style="color:#fff;">{{'BASE.MORE' | translate}}</a> </div>
  </div>
</section>
<app-footer></app-footer>
