import { AuthService } from 'src/app/service/auth.service';
import { CustomService } from 'src/app/service/custom.service';
import { Observable, Subject } from 'rxjs';
import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { FormGroup, FormBuilder, Validators } from "@angular/forms";
import { CustomFunctions } from 'src/app/shared/customFunctions/customFunctions';
declare var $: any;

@Component({
  selector: 'app-hire-expert',
  templateUrl: './hireExpert.component.html',
  styleUrls: ['./hireExpert.component.scss']
})

export class HireExpertComponent implements OnInit {
  expertskillslist: any;
  usersfilterlist: any;
  searchfilterForm!: FormGroup;
  hireForm!: FormGroup;
  hirenowprojects: any;
  projectsObj = [];
  hireformsubmit: any;
  error_msg: any;
  total: any;
  current_page: any;
  next_page_url: any;
  per_page: any;
  usersList$ = new Subject();
  usersList: Array<any> = [];
  activeProject$!: Observable<any>;
  skills$!: Observable<any>;
  services$!: Observable<any>;
  subServices$!: Observable<any>;
  take = 10;
  skip = 0;
  showModal!: boolean;
  multiUserModal: boolean = false;
  submitted = false;
  activeProjectsCount = 0;
  projectId: string = "";
  privateMsg: string = "";
  expertIDs: Array<string> = [];
  constructor(
    public customFunctions: CustomFunctions,
    private router: Router,
    private formBuilder: FormBuilder,
    private customService: CustomService,
    private authService: AuthService,
    private activatedRoute: ActivatedRoute
  ) { }
  ngOnInit() {
    this.skills$ = this.customService.Get(`Skill`);
    this.services$ = this.customService.Get(`Service`);
    this.activeProject$ = this.customService.Get(`Project/GetInviteProjects`);
    this.activeProject$.subscribe(projects => {
      this.activeProjectsCount = projects.length;
    });

    // skills: ['', Validators.required],
    // rate: ['', Validators.required],
    this.searchfilterForm = this.formBuilder.group({
      search: ['', Validators.required],
      skills: ['', Validators.required],
      rate: ['', Validators.required],
      ServiceId: [null, Validators.required]
    });

    this.hireForm = this.formBuilder.group({
      UserId: ['', Validators.required],
      InviteMessage: ['', Validators.required],
      ProjectId: ['', Validators.required],
    });

    this.projectId = this.activatedRoute.snapshot.queryParamMap.get('qsProjectId') || '';
    let serviceId = Number(this.activatedRoute.snapshot.queryParamMap.get('qsServiceID'));
    if( serviceId && serviceId > 0){
      this.searchfilterForm.get('ServiceId')?.setValue(serviceId);
      this.searchHire();
    }else {
      this.customService.Get(`Identity/2`).subscribe(res => {
        this.usersList = res.data;
        console.log('data ', res);
        // this.getExpertCount();

      });
    }


  }

  getExpertCount(){
    this.customService.Get(`Identity/GetDashboardCounts`).subscribe(res => {
      console.log('GetDashboardCounts ', res);

    });
  }

  onSelectService(serviceId: any) {
    console.log('serviceId ', serviceId);
    this.skills$ = this.customService.Get(`Skill/GetByService/${serviceId}`);
  }
  ngDoCheck() {

  }
  searchHire() {
    let serviceId=0;
    if(this.searchfilterForm.controls.ServiceId.value!=null){
      serviceId=this.searchfilterForm.controls.ServiceId.value
    }
    console.log('check',serviceId)
    const data = {
      serviceId: serviceId,
      fullName: this.searchfilterForm.controls.search.value,
      take: this.take,
      skip: this.skip
    }
    this.customService.post(data, `Identity/FindHire`).subscribe(res => {
      this.usersList = res.data;
      console.log('FindHire  ', res);
    });
  }
  expertskills() {

  }
  geclickexpertfilter() {

  }

  onClick(userId: string) {
    if (this.authService.isLoggedIn()) {
      this.showModal = true;
      this.hireForm.patchValue({
        UserId: userId
      });
    } else {
      this.router.navigate(['/account/login'])
    }

  }

  onSendInvitationClick() {
    if (this.authService.isLoggedIn()) {
      if(this.expertIDs.length < 1){
        this.customFunctions.showResponseMessage('Please Select Experts', false);
        return;
      }
      if(this.expertIDs.length > 5){
        this.customFunctions.showResponseMessage('Please only 5 experts at a time ', false);
        return;
      }
      this.multiUserModal = true;
      // this.hireForm.patchValue({
      //   UserId: userId
      // });
    } else {
      this.router.navigate(['/account/login'])
    }

  }

  onPrivateMsgUpdate(e:any){
    this.privateMsg = e.target.value;
  }

  onMultiHideClick(){
    console.log(this.privateMsg);

    if(this.privateMsg == ''){
      this.customFunctions.showResponseMessage('Private Message is required.', false);
        return;
    }

    const data = {
      userId: this.expertIDs,
      projectId: this.projectId,
      inviteMessage: this.privateMsg,
    }
    this.customService.post(data, 'ProjectOffer/MultipleUsersSendInvite').subscribe(res => {
      if (res) {
        this.customFunctions.showResponseMessage(res.description, res.code);
        if (res.code <= 300) {
          this.multiUserModal = false;
          this.privateMsg = '';
        }
      }
    });


  }

  goToUserDetailsPage(userId: string): void {
    if (this.authService.isLoggedIn()) {
      this.router.navigate(['/account/userDetails/' + userId])
    } else {
      this.router.navigate(['/account/login'])
    }
  }
  hide() {
    this.hireForm.reset();
    this.showModal = false;
    // this.showModal = false;
  }

  onSubmit() {
  }

  fieldsChange(values:any, userid: string):void {
    if(values.currentTarget.checked){
      this.expertIDs.push(userid);
    }else {
      this.expertIDs = this.expertIDs.filter(oi => oi != userid);
    }
  }

  onMultipleSubmitClick() {
    this.submitted = true;
    if (this.hireForm.invalid) {
      this.customFunctions.showResponseMessage('Please validate All Filed is Correct ', false);
      return;
    }
    const data = {
      userId: this.hireForm.controls.UserId.value,
      projectId: this.hireForm.controls.ProjectId.value,
      inviteMessage: this.hireForm.controls.InviteMessage.value,
    }
    this.customService.post(data, 'ProjectOffer/SendInvite').subscribe(res => {
      if (res) {
        this.customFunctions.showResponseMessage(res.description, res.code);
        if (res.code <= 300) {
          this.hireForm.reset();
          this.showModal = false;
        }
      }
    });
    var projects = this.projectsObj;
    var message = this.hireForm.value.inputMessage;
    var obj = {
      "message": message,
      "class": 'Project',
      "projects": projects
    };

    //let pushedtime = { "projects": {projects}};
    // let pu = {"projects":[{"project_id":99},{"project_id":100}]};
  }

  onHire() {
    this.submitted = true;
    if (this.hireForm.invalid) {
      this.customFunctions.showResponseMessage('Please validate All Filed is Correct ', false);
      return;
    }
    const data = {
      userId: this.hireForm.controls.UserId.value,
      projectId: this.hireForm.controls.ProjectId.value,
      inviteMessage: this.hireForm.controls.InviteMessage.value,
    }
    this.customService.post(data, 'ProjectOffer/SendInvite').subscribe(res => {
      if (res) {
        this.customFunctions.showResponseMessage(res.description, res.code);
        if (res.code <= 300) {
          this.hireForm.reset();
          this.showModal = false;
        }
      }
    });
    var projects = this.projectsObj;
    var message = this.hireForm.value.inputMessage;
    var obj = {
      "message": message,
      "class": 'Project',
      "projects": projects
    };

    //let pushedtime = { "projects": {projects}};
    // let pu = {"projects":[{"project_id":99},{"project_id":100}]};
  }

  onProjectnameChange(event: any, value: any) {

  }

  p(event: any) {

  }

}
<main>
  <!-- inner banner -->
  <section class="inner-bg">
    <div class="container">
      <div class="row">
        <div class="col-xl-12 col-lg-12 col-md-12 col-12">
          <div class="inner-title">
            <h1 *ngIf="!customFunctions.IsRTL()">
              <img src="assets/images/icon/hire.png" alt="" class="img-fluid icon-img" style="max-width: 7%;">

              {{'NAV.HIRE_EXPERT' | translate}}
            </h1>

            <h1 *ngIf="customFunctions.IsRTL()" style="text-align: right;">
              <img src="assets/images/icon/hire.png" alt="" class="img-fluid icon-img" style="max-width: 7%;">

              {{'NAV.HIRE_EXPERT' | translate}}
            </h1>
          </div>
        </div>
      </div>
    </div>
  </section>

  <section class="pt-90" style="padding-top: 0px; margin-top:-100px;">
    <div class="container">
      <div class="row">
        <div class="card find-project col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
          <div class="card-body">
            <div class="search-form prjct-form">

              <form class="form-s" [formGroup]="searchfilterForm" (ngSubmit)="onSubmit()">
                <div class="row">
                  <div class="col-md-4">
                    <!-- <div class="input-group mt-3 mb-3"> -->
                    <input type="text" formControlName="search" class="form-control m-1"
                      placeholder="{{'BASE.SEARCH_BY_WORDS' | translate}}">
                    <!-- </div> -->
                  </div>
                  <div class="col-md-4">
                    <!-- <div class="input-group-prepend ml-3"> -->
                    <ng-select formControlName="ServiceId" class="m-1" (change)="onSelectService($event)">
                      <ng-option value="">{{ 'What Type of service?' }}</ng-option>
                      <ng-option *ngFor="let service of services$ |async" [value]="service.id">
                        {{service.name}}
                      </ng-option>
                    </ng-select>
                    <!-- </div> -->
                  </div>

                  <div class="col-md-4">
                    <button type="button" (click)="searchHire()" class="btn btn-green pt-2"> search</button>
                    <button *ngIf="projectId != ''" [disabled]="expertIDs.length <= 0" type="button" (click)="onSendInvitationClick()" class="ml-2 btn btn-blue pt-2">Send Invitation</button>
                    <!-- <div class="col-md-6 ">
                      <ng-select formControlName="skills" class="m-1">
                        <ng-option value="">{{ 'Type to Filter by Skills' }}</ng-option>
                        <ng-option *ngFor="let skill of skills$ | async" [value]="skill.id">{{skill.name}}</ng-option>
                      </ng-select>
                    </div> -->
                    <!-- <div class="col-md-6">
                      <ng-select formControlName="rate" class="m-1">
                        <ng-option value="">{{ 'Any Hourly Rate' }}</ng-option>
                        <ng-option value="hourly_rate_min=10"> $10.00/{{ 'hour' }}</ng-option>
                        <ng-option value="hourly_rate_max=20&hourly_rate_min=10"> $10.00 - $20.00/{{ 'hour' }}
                        </ng-option>
                        <ng-option value="hourly_rate_max=30&hourly_rate_min=21"> $21.00 - $30.00/{{ 'hour' }}
                        </ng-option>
                        <ng-option value="hourly_rate_max=40&hourly_rate_min=31"> $31.00 - $40.00/{{ 'hour' }}
                        </ng-option>
                        <ng-option value="hourly_rate_min=40"> $40.00/{{ 'hour' }}</ng-option>
                      </ng-select>
                    </div> -->
                  </div>
                </div>

              </form>

            </div>
          </div>
        </div>
      </div>
    </div>
  </section>

  <section>
    <div class="container">
      <div class="row">
        <!-- <div class="no-result col-md-12">
          <p *ngIf="!customFunctions.IsRTL()">{{'HIRE_EXPERT.SUB_TITLE' | translate}}</p>
          <p *ngIf="customFunctions.IsRTL()" style="text-align: right;">{{'HIRE_EXPERT.SUB_TITLE' | translate}}</p>
        </div> -->
        <div class="col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12">
          <div class="row" style="margin-top: 50px;">
            <div class="col-md-3 card prjct-card m-1" *ngFor="let users of  usersList; let i = index">
              <div class="card-body">
                <!-- <div class="row"> -->
