    i0.ɵɵtext(2, "\u0623\u0647\u0644\u0627 \u0628\u0643 \u0641\u064A \u0645\u0648\u0642\u0639\u0646\u0627");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(3, "p");
    i0.ɵɵtext(4, "\u062A\u062D\u062F\u062F \u0647\u0630\u0647 \u0627\u0644\u0634\u0631\u0648\u0637 \u0648\u0627\u0644\u0623\u062D\u0643\u0627\u0645 \u0627\u0644\u0642\u0648\u0627\u0639\u062F \u0648\u0627\u0644\u0644\u0648\u0627\u0626\u062D \u0627\u0644\u062E\u0627\u0635\u0629 \u0628\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0634\u0631\u0643\u062A\u0646\u0627");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(5, "p");
    i0.ɵɵtext(6, " \u0645\u0646 \u062E\u0644\u0627\u0644 \u0627\u0644\u0648\u0635\u0648\u0644 \u0625\u0644\u0649 \u0647\u0630\u0627 \u0627\u0644\u0645\u0648\u0642\u0639\u060C \u0646\u0641\u062A\u0631\u0636 \u0623\u0646\u0643 \u062A\u0642\u0628\u0644 \u0647\u0630\u0647 \u0627\u0644\u0634\u0631\u0648\u0637 \u0648\u0627\u0644\u0623\u062D\u0643\u0627\u0645. \u0644\u0627 \u062A\u0633\u062A\u0645\u0631 \u0641\u064A \u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0644\u0649 \u0634\u0628\u0643\u0629 \u0627\u0644\u0625\u0646\u062A\u0631\u0646\u062A \u0625\u0630\u0627 \u0643\u0646\u062A \u0644\u0627 \u062A\u0648\u0627\u0641\u0642 \u0639\u0644\u0649 \u0623\u062E\u0630 \u062C\u0645\u064A\u0639 \u0627\u0644\u0634\u0631\u0648\u0637 \u0648\u0627\u0644\u0623\u062D\u0643\u0627\u0645 \u0627\u0644\u0645\u0630\u0643\u0648\u0631\u0629 \u0641\u064A \u0647\u0630\u0647 \u0627\u0644\u0635\u0641\u062D\u0629. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(7, "p");
    i0.ɵɵtext(8, " \u062A\u0646\u0637\u0628\u0642 \u0627\u0644\u0645\u0635\u0637\u0644\u062D\u0627\u062A \u0627\u0644\u062A\u0627\u0644\u064A\u0629 \u0639\u0644\u0649 \u0647\u0630\u0647 \u0627\u0644\u0634\u0631\u0648\u0637 \u0648\u0627\u0644\u0623\u062D\u0643\u0627\u0645 \u0648\u0628\u064A\u0627\u0646 \u0627\u0644\u062E\u0635\u0648\u0635\u064A\u0629 \u0648\u0625\u0634\u0639\u0627\u0631 \u0625\u062E\u0644\u0627\u0621 \u0627\u0644\u0645\u0633\u0624\u0648\u0644\u064A\u0629 \u0648\u062C\u0645\u064A\u0639 \u0627\u0644\u0627\u062A\u0641\u0627\u0642\u064A\u0627\u062A: \u064A\u0634\u064A\u0631 \u0645\u0635\u0637\u0644\u062D \"\u0627\u0644\u0639\u0645\u064A\u0644\" \u0648 \"\u0623\u0646\u062A\" \u0648 \"\u0627\u0644\u062E\u0627\u0635 \u0628\u0643\" \u0625\u0644\u064A\u0643\u060C \u0648\u0627\u0644\u0634\u062E\u0635 \u0627\u0644\u0630\u064A \u064A\u0642\u0648\u0645 \u0628\u062A\u0633\u062C\u064A\u0644 \u0627\u0644\u062F\u062E\u0648\u0644 \u0625\u0644\u0649 \u0647\u0630\u0627 \u0627\u0644\u0645\u0648\u0642\u0639 \u064A\u062A\u0648\u0627\u0641\u0642 \u0645\u0639 \u0634\u0631\u0648\u0637 \u0648\u0623\u062D\u0643\u0627\u0645 \u0627\u0644\u0634\u0631\u0643\u0629. \u062A\u0634\u064A\u0631 \"\u0627\u0644\u0634\u0631\u0643\u0629\" \u0648 \"\u0623\u0646\u0641\u0633\u0646\u0627\" \u0648 \"\u0644\u0646\u0627\" \u0648 \"\u0646\u062D\u0646\" \u0625\u0644\u0649 \u0634\u0631\u0643\u062A\u0646\u0627. \u064A\u0634\u064A\u0631 \"\u0627\u0644\u0637\u0631\u0641\" \u0623\u0648 \"\u0627\u0644\u0623\u0637\u0631\u0627\u0641\" \u0623\u0648 \"\u0646\u062D\u0646\" \u0625\u0644\u0649 \u0643\u0644 \u0645\u0646 \u0627\u0644\u0639\u0645\u064A\u0644 \u0648\u0623\u0646\u0641\u0633\u0646\u0627. \u062A\u0634\u064A\u0631 \u062C\u0645\u064A\u0639 \u0627\u0644\u0634\u0631\u0648\u0637 \u0625\u0644\u0649 \u0627\u0644\u0639\u0631\u0636 \u0648\u0627\u0644\u0642\u0628\u0648\u0644 \u0648\u0627\u0644\u0646\u0638\u0631 \u0641\u064A \u0627\u0644\u062F\u0641\u0639 \u0627\u0644\u0644\u0627\u0632\u0645 \u0644\u0644\u0627\u0636\u0637\u0644\u0627\u0639 \u0628\u0639\u0645\u0644\u064A\u0629 \u0645\u0633\u0627\u0639\u062F\u062A\u0646\u0627 \u0644\u0644\u0639\u0645\u064A\u0644 \u0628\u0627\u0644\u0637\u0631\u064A\u0642\u0629 \u0627\u0644\u0623\u0646\u0633\u0628 \u0644\u0644\u063A\u0631\u0636 \u0627\u0644\u0635\u0631\u064A\u062D \u0627\u0644\u0645\u062A\u0645\u062B\u0644 \u0641\u064A \u062A\u0644\u0628\u064A\u0629 \u0627\u062D\u062A\u064A\u0627\u062C\u0627\u062A \u0627\u0644\u0639\u0645\u064A\u0644 \u0641\u064A\u0645\u0627 \u064A\u062A\u0639\u0644\u0642 \u0628\u062A\u0648\u0641\u064A\u0631 \u062E\u062F\u0645\u0627\u062A \u0627\u0644\u0634\u0631\u0643\u0629 \u0627\u0644\u0645\u0639\u0644\u0646\u0629\u060C \u0648\u0641\u0642\u064B\u0627 \u0644\u0640 \u0644\u0644\u0642\u0627\u0646\u0648\u0646 \u0627\u0644\u0633\u0627\u0626\u062F \u0641\u064A \u0647\u0648\u0644\u0646\u062F\u0627. \u0623\u064A \u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0627\u0644\u0645\u0635\u0637\u0644\u062D\u0627\u062A \u0627\u0644\u0645\u0630\u0643\u0648\u0631\u0629 \u0623\u0639\u0644\u0627\u0647 \u0623\u0648 \u063A\u064A\u0631\u0647\u0627 \u0645\u0646 \u0627\u0644\u0643\u0644\u0645\u0627\u062A \u0641\u064A \u0635\u064A\u063A\u0629 \u0627\u0644\u0645\u0641\u0631\u062F \u0648\u0627\u0644\u062C\u0645\u0639 \u0648 / \u0623\u0648 \u0647\u0648 / \u0647\u064A \u0623\u0648 \u0647\u0645 \u060C \u064A\u062A\u0645 \u0627\u0639\u062A\u0628\u0627\u0631\u0647 \u0642\u0627\u0628\u0644\u0627\u064B \u0644\u0644\u062A\u0628\u0627\u062F\u0644 \u0648\u0628\u0627\u0644\u062A\u0627\u0644\u064A \u064A\u0634\u064A\u0631 \u0625\u0644\u0649 \u0646\u0641\u0633\u0647 ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(9, "h3");
    i0.ɵɵtext(10, "\u0627\u0644\u0634\u0631\u0648\u0637 \u0627\u0644\u0639\u0627\u0645\u0629");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(11, "p");
    i0.ɵɵtext(12, " \u064A\u0645\u0643\u0646 \u0644\u0644\u0645\u0633\u062A\u062E\u062F\u0645\u064A\u0646 \u0627\u0644\u0645\u0633\u062C\u0644\u064A\u0646 \u0641\u0642\u0637 \u0627\u0644\u0634\u0631\u0627\u0621 \u0648\u0627\u0644\u0628\u064A\u0639 \u0639\u0644\u0649 \u0645\u0646\u0635\u0629 \u0628\u062D\u062B\u064A. \u0627\u0644\u062A\u0633\u062C\u064A\u0644 \u0645\u062C\u0627\u0646\u064A. \u0639\u0646\u062F \u0627\u0644\u062A\u0633\u062C\u064A\u0644 \u0644\u0644\u062D\u0635\u0648\u0644 \u0639\u0644\u0649 \u062D\u0633\u0627\u0628 \u060C \u0641\u0625\u0646\u0643 \u062A\u0648\u0627\u0641\u0642 \u0639\u0644\u0649 \u062A\u0632\u0648\u064A\u062F\u0646\u0627 \u0628\u0645\u0639\u0644\u0648\u0645\u0627\u062A \u062F\u0642\u064A\u0642\u0629 \u0648\u0643\u0627\u0645\u0644\u0629 \u0648\u0645\u062D\u062F\u062B\u0629 \u0648\u064A\u062C\u0628 \u0623\u0644\u0627 \u062A\u0646\u0634\u0626 \u062D\u0633\u0627\u0628\u064B\u0627 \u0644\u0623\u063A\u0631\u0627\u0636 \u0627\u062D\u062A\u064A\u0627\u0644\u064A\u0629 \u0623\u0648 \u0645\u0636\u0644\u0644\u0629. \u0623\u0646\u062A \u0648\u062D\u062F\u0643 \u0627\u0644\u0645\u0633\u0624\u0648\u0644 \u0639\u0646 \u0623\u064A \u0646\u0634\u0627\u0637 \u0639\u0644\u0649 \u062D\u0633\u0627\u0628\u0643 \u0648\u0639\u0646 \u0627\u0644\u062D\u0641\u0627\u0638 \u0639\u0644\u0649 \u0633\u0631\u064A\u0629 \u0648\u0623\u0645\u0646 \u0643\u0644\u0645\u0629 \u0627\u0644\u0645\u0631\u0648\u0631 \u0627\u0644\u062E\u0627\u0635\u0629 \u0628\u0643. \u0646\u062D\u0646 \u0644\u0633\u0646\u0627 \u0645\u0633\u0624\u0648\u0644\u064A\u0646 \u0639\u0646 \u0623\u064A \u0623\u0641\u0639\u0627\u0644 \u0623\u0648 \u0625\u063A\u0641\u0627\u0644\u0627\u062A \u0645\u0646 \u062C\u0627\u0646\u0628\u0643 \u0641\u064A\u0645\u0627 \u064A\u062A\u0639\u0644\u0642 \u0628\u062D\u0633\u0627\u0628\u0643. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(13, "h3");
    i0.ɵɵtext(14, "\u0645\u0644\u0641\u0627\u062A \u062A\u0639\u0631\u064A\u0641 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(15, "p");
    i0.ɵɵtext(16, " \u0646\u062D\u0646 \u0646\u0648\u0638\u0641 \u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0645\u0644\u0641\u0627\u062A \u062A\u0639\u0631\u064A\u0641 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 . \u0645\u0646 \u062E\u0644\u0627\u0644 \u0627\u0644\u0648\u0635\u0648\u0644 \u0625\u0644\u0649 \u0645\u0648\u0642\u0639\u0646\u0627\u060C \u0641\u0625\u0646\u0643 \u062A\u0648\u0627\u0641\u0642 \u0639\u0644\u0649 \u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0645\u0644\u0641\u0627\u062A \u062A\u0639\u0631\u064A\u0641 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u0628\u0627\u0644\u0627\u062A\u0641\u0627\u0642 \u0645\u0639 \u0633\u064A\u0627\u0633\u0629 \u0627\u0644\u062E\u0635\u0648\u0635\u064A\u0629 \u0627\u0644\u062E\u0627\u0635\u0629 \u0628\u0634\u0631\u0643\u062A\u0646\u0627. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(17, "p");
    i0.ɵɵtext(18, "\u062F\u0645 \u0645\u0639\u0638\u0645 \u0645\u0648\u0627\u0642\u0639 \u0627\u0644\u0648\u064A\u0628 \u0627\u0644\u062A\u0641\u0627\u0639\u0644\u064A\u0629 \u0645\u0644\u0641\u0627\u062A \u062A\u0639\u0631\u064A\u0641 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u0644\u0644\u0633\u0645\u0627\u062D \u0644\u0646\u0627 \u0628\u0627\u0633\u062A\u0631\u062F\u0627\u062F \u062A\u0641\u0627\u0635\u064A\u0644 \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u0644\u0643\u0644 \u0632\u064A\u0627\u0631\u0629. \u064A\u0633\u062A\u062E\u062F\u0645 \u0645\u0648\u0642\u0639\u0646\u0627 \u0645\u0644\u0641\u0627\u062A \u062A\u0639\u0631\u064A\u0641 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u0644\u062A\u0645\u0643\u064A\u0646 \u0648\u0638\u0627\u0626\u0641 \u0645\u0646\u0627\u0637\u0642 \u0645\u0639\u064A\u0646\u0629 \u0644\u062A\u0633\u0647\u064A\u0644 \u0632\u064A\u0627\u0631\u0629 \u0627\u0644\u0623\u0634\u062E\u0627\u0635 \u0644\u0645\u0648\u0642\u0639\u0646\u0627. \u0642\u062F \u064A\u0633\u062A\u062E\u062F\u0645 \u0628\u0639\u0636 \u0627\u0644\u0634\u0631\u0643\u0627\u0621 \u0627\u0644\u062A\u0627\u0628\u0639\u064A\u0646 / \u0627\u0644\u0645\u0639\u0644\u0646\u064A\u0646 \u0623\u064A\u0636\u064B\u0627 \u0645\u0644\u0641\u0627\u062A \u062A\u0639\u0631\u064A\u0641 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(19, "h3");
    i0.ɵɵtext(20, "\u0627\u0644\u062A\u0631\u062E\u064A\u0635");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(21, "p");
    i0.ɵɵtext(22, "\u0645\u0627 \u0644\u0645 \u064A\u064F\u0646\u0635 \u0639\u0644\u0649 \u062E\u0644\u0627\u0641 \u0630\u0644\u0643 \u060C \u064A\u0645\u062A\u0644\u0643 \u0627\u0644\u0645\u0631\u062E\u0635\u0648\u0646 \u0627\u0644\u062A\u0627\u0628\u0639\u0648\u0646 \u0644\u0647\u0627 \u062D\u0642\u0648\u0642 \u0627\u0644\u0645\u0644\u0643\u064A\u0629 \u0627\u0644\u0641\u0643\u0631\u064A\u0629 \u0644\u062C\u0645\u064A\u0639 \u0627\u0644\u0645\u0648\u0627\u062F \u0627\u0644\u0645\u0648\u062C\u0648\u062F\u0629 \u0639\u0644\u0649 \u0645\u0648\u0642\u0639\u0646\u0627. \u062C\u0645\u064A\u0639 \u062D\u0642\u0648\u0642 \u0627\u0644\u0645\u0644\u0643\u064A\u0629 \u0627\u0644\u0641\u0643\u0631\u064A\u0629 \u0645\u062D\u0641\u0648\u0638\u0629. \u064A\u0645\u0643\u0646\u0643 \u0627\u0644\u0648\u0635\u0648\u0644 \u0625\u0644\u0649 \u0647\u0630\u0627 \u0645\u0646 \u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0644\u0649 \u0627\u0644\u0625\u0646\u062A\u0631\u0646\u062A \u0644\u0627\u0633\u062A\u062E\u062F\u0627\u0645\u0643 \u0627\u0644\u0634\u062E\u0635\u064A \u0645\u0639 \u0645\u0631\u0627\u0639\u0627\u0629 \u0627\u0644\u0642\u064A\u0648\u062F \u0627\u0644\u0645\u0646\u0635\u0648\u0635 \u0639\u0644\u064A\u0647\u0627 \u0641\u064A \u0647\u0630\u0647 \u0627\u0644\u0634\u0631\u0648\u0637 \u0648\u0627\u0644\u0623\u062D\u0643\u0627\u0645. \u062A\u0628\u062F\u0623 \u0647\u0630\u0647 \u0627\u0644\u0627\u062A\u0641\u0627\u0642\u064A\u0629 \u0641\u064A \u062A\u0627\u0631\u064A\u062E \u0627\u0644\u0645\u0648\u0627\u0641\u0642\u0629 .");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(23, "p");
    i0.ɵɵtext(24, "\u0644\u0627 \u064A\u062C\u0628 \u0639\u0644\u064A\u0643:");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(25, "ul", 14);
    i0.ɵɵelementStart(26, "li");
    i0.ɵɵtext(27, "\u0625\u0639\u0627\u062F\u0629 \u0646\u0634\u0631 \u0627\u0644\u0645\u0648\u0627\u062F \u0645\u0646 \u0645\u0648\u0642\u0639\u0646\u0627");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(28, "li");
    i0.ɵɵtext(29, "\u0628\u064A\u0639 \u0623\u0648 \u062A\u0623\u062C\u064A\u0631 \u0623\u0648 \u062A\u0631\u062E\u064A\u0635 \u0627\u0644\u0645\u0648\u0627\u062F \u0645\u0646 \u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0644\u0649 \u0627\u0644\u0625\u0646\u062A\u0631\u0646\u062A");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(30, "li");
    i0.ɵɵtext(31, "\u0625\u0639\u0627\u062F\u0629 \u0625\u0646\u062A\u0627\u062C \u0646\u0633\u062E \u0627\u0644\u0645\u0648\u0627\u062F \u0645\u0646 \u0645\u0648\u0642\u0639\u0646\u0627");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(32, "li");
    i0.ɵɵtext(33, "\u0625\u0639\u0627\u062F\u0629 \u062A\u0648\u0632\u064A\u0639 \u0627\u0644\u0645\u062D\u062A\u0648\u0649 \u0645\u0646 \u0645\u0648\u0642\u0639\u0646\u0627");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(34, "li");
    i0.ɵɵtext(35, "\u062A\u0628\u062F\u0623 \u0647\u0630\u0647 \u0627\u0644\u0627\u062A\u0641\u0627\u0642\u064A\u0629 \u0641\u064A \u062A\u0627\u0631\u064A\u062E\u0647\u0627");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(36, "li");
    i0.ɵɵtext(37, "\u062A\u0648\u0641\u0631 \u0623\u062C\u0632\u0627\u0621 \u0645\u0646 \u0647\u0630\u0627 \u0627\u0644\u0645\u0648\u0642\u0639 \u0641\u0631\u0635\u0629 \u0644\u0644\u0645\u0633\u062A\u062E\u062F\u0645\u064A\u0646 \u0644\u0646\u0634\u0631 \u0648\u062A\u0628\u0627\u062F\u0644 \u0627\u0644\u0622\u0631\u0627\u0621 \u0648\u0627\u0644\u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0641\u064A \u0642\u0633\u0645 \u0627\u0644\u062F\u0631\u062F\u0634\u0629");
