    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(38, "p");
    i0.ɵɵtext(39, "\u062A\u0648\u0641\u0631 \u0623\u062C\u0632\u0627\u0621 \u0645\u0646 \u0647\u0630\u0627 \u0627\u0644\u0645\u0648\u0642\u0639 \u0641\u0631\u0635\u0629 \u0644\u0644\u0645\u0633\u062A\u062E\u062F\u0645\u064A\u0646 \u0644\u0646\u0634\u0631 \u0648\u062A\u0628\u0627\u062F\u0644 \u0627\u0644\u0622\u0631\u0627\u0621 \u0648\u0627\u0644\u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0641\u064A \u0642\u0633\u0645 \u0627\u0644\u062F\u0631\u062F\u0634\u0629. \u0646\u062A\u062D\u062A\u0641\u0638 \u0628\u0627\u0644\u062D\u0642 \u0641\u064A \u0645\u0631\u0627\u0642\u0628\u0629 \u062C\u0645\u064A\u0639 \u0627\u0644\u062A\u0639\u0644\u064A\u0642\u0627\u062A \u0648\u0625\u0632\u0627\u0644\u0629 \u0623\u064A \u062A\u0639\u0644\u064A\u0642\u0627\u062A \u064A\u0645\u0643\u0646 \u0627\u0639\u062A\u0628\u0627\u0631\u0647\u0627 \u063A\u064A\u0631 \u0645\u0644\u0627\u0626\u0645\u0629 \u0623\u0648 \u0645\u0633\u064A\u0626\u0629 \u0623\u0648 \u062A\u0633\u0628\u0628 \u0627\u0646\u062A\u0647\u0627\u0643\u064B\u0627 \u0644\u0647\u0630\u0647 \u0627\u0644\u0634\u0631\u0648\u0637 \u0648\u0627\u0644\u0623\u062D\u0643\u0627\u0645.");
