    i0.ɵɵelementStart(40, "p");
    i0.ɵɵtext(41, " \u0623\u0646\u062A \u062A\u0636\u0645\u0646 \u0648\u062A\u0645\u062B\u0644 \u0645\u0627 \u064A\u0644\u064A: ");
