    i0.ɵɵelementStart(45, "li");
    i0.ɵɵtext(46, "\u0644\u0627 \u062A\u0646\u062A\u0647\u0643 \u0627\u0644\u062A\u0639\u0644\u064A\u0642\u0627\u062A \u0623\u064A \u062D\u0642 \u0645\u0646 \u062D\u0642\u0648\u0642 \u0627\u0644\u0645\u0644\u0643\u064A\u0629 \u0627\u0644\u0641\u0643\u0631\u064A\u0629\u060C \u0628\u0645\u0627 \u0641\u064A \u0630\u0644\u0643 \u0639\u0644\u0649 \u0633\u0628\u064A\u0644 \u0627\u0644\u0645\u062B\u0627\u0644 \u0644\u0627 \u0627\u0644\u062D\u0635\u0631 \u062D\u0642\u0648\u0642 \u0627\u0644\u0646\u0634\u0631 \u0623\u0648 \u0628\u0631\u0627\u0621\u0627\u062A \u0627\u0644\u0627\u062E\u062A\u0631\u0627\u0639 \u0623\u0648 \u0627\u0644\u0639\u0644\u0627\u0645\u0627\u062A \u0627\u0644\u062A\u062C\u0627\u0631\u064A\u0629 \u0644\u0623\u064A \u0637\u0631\u0641 \u062B\u0627\u0644\u062B ");
