    i0.ɵɵelementStart(47, "li");
    i0.ɵɵtext(48, "\u0644\u0627 \u062A\u062D\u062A\u0648\u064A \u0627\u0644\u062A\u0639\u0644\u064A\u0642\u0627\u062A \u0639\u0644\u0649 \u0623\u064A \u0645\u0648\u0627\u062F \u062A\u0634\u0647\u064A\u0631\u064A\u0629 \u0623\u0648 \u0645\u0633\u064A\u0626\u0629 \u0623\u0648 \u063A\u064A\u0631 \u0644\u0627\u0626\u0642\u0629 \u0623\u0648 \u063A\u064A\u0631 \u0642\u0627\u0646\u0648\u0646\u064A\u0629 \u0628\u0623\u064A \u0634\u0643\u0644 \u0645\u0645\u0627 \u064A\u0639\u062F \u0627\u0646\u062A\u0647\u0627\u0643\u064B\u0627 \u0644\u0644\u062E\u0635\u0648\u0635\u064A\u0629 ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(49, "li");
    i0.ɵɵtext(50, "\u0644\u0646 \u064A\u062A\u0645 \u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0627\u0644\u062A\u0639\u0644\u064A\u0642\u0627\u062A \u0644\u0627\u0644\u062A\u0645\u0627\u0633 \u0623\u0648 \u0627\u0644\u062A\u0631\u0648\u064A\u062C \u0644\u0644\u0623\u0639\u0645\u0627\u0644 \u0623\u0648 \u0627\u0644\u0623\u0646\u0634\u0637\u0629 \u0627\u0644\u062A\u062C\u0627\u0631\u064A\u0629 \u0627\u0644\u0645\u062E\u0635\u0635\u0629 \u0623\u0648 \u0627\u0644\u062D\u0627\u0644\u064A\u0629 \u0623\u0648 \u0646\u0634\u0627\u0637 \u063A\u064A\u0631 \u0642\u0627\u0646\u0648\u0646\u064A ");
