    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(51, "p");
    i0.ɵɵtext(52, " \u0623\u0646\u062A \u062A\u0645\u0646\u062D \u0628\u0645\u0648\u062C\u0628 \u0647\u0630\u0627 \u062A\u0631\u062E\u064A\u0635\u064B\u0627 \u063A\u064A\u0631 \u062D\u0635\u0631\u064A \u0644\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0648\u0625\u0639\u0627\u062F\u0629 \u0625\u0646\u062A\u0627\u062C \u0648\u062A\u062D\u0631\u064A\u0631 \u0648\u062A\u0641\u0648\u064A\u0636 \u0627\u0644\u0622\u062E\u0631\u064A\u0646 \u0644\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0648\u0625\u0639\u0627\u062F\u0629 \u0625\u0646\u062A\u0627\u062C \u0648\u062A\u062D\u0631\u064A\u0631 \u0623\u064A \u0645\u0646 \u062A\u0639\u0644\u064A\u0642\u0627\u062A\u0643 \u0641\u064A \u0623\u064A \u0648\u062C\u0645\u064A\u0639 \u0627\u0644\u0623\u0634\u0643\u0627\u0644 \u0623\u0648 \u0627\u0644\u062A\u0646\u0633\u064A\u0642\u0627\u062A \u0623\u0648 \u0627\u0644\u0648\u0633\u0627\u0626\u0637 ");
