    i0.ɵɵelementStart(66, "li");
    i0.ɵɵtext(67, "\u0627\u0644\u0634\u0631\u0643\u0627\u062A \u0627\u0644\u0645\u0639\u062A\u0645\u062F\u0629 \u0639\u0644\u0649 \u0645\u0633\u062A\u0648\u0649 \u0627\u0644\u0646\u0638\u0627\u0645 \u0628\u0627\u0633\u062A\u062B\u0646\u0627\u0621 \u0637\u0644\u0628 \u0627\u0644\u0645\u0646\u0638\u0645\u0627\u062A \u063A\u064A\u0631 \u0627\u0644\u0631\u0628\u062D\u064A\u0629 \u0648\u0645\u0631\u0627\u0643\u0632 \u0627\u0644\u062A\u0633\u0648\u0642 \u0627\u0644\u062E\u064A\u0631\u064A\u0629 \u0648\u0645\u062C\u0645\u0648\u0639\u0627\u062A \u062C\u0645\u0639 \u0627\u0644\u062A\u0628\u0631\u0639\u0627\u062A \u0627\u0644\u062E\u064A\u0631\u064A\u0629 \u0627\u0644\u062A\u064A \u0642\u062F \u0644\u0627 \u062A\u0631\u062A\u0628\u0637 \u0627\u0631\u062A\u0628\u0627\u0637\u064B\u0627 \u062A\u0634\u0639\u0628\u064A\u064B\u0627 \u0628\u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0644\u0649 \u0627\u0644\u0648\u064A\u0628 ");
