    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(92, "p");
    i0.ɵɵtext(93, " \u0633\u0646\u0648\u0627\u0641\u0642 \u0639\u0644\u0649 \u0637\u0644\u0628\u0627\u062A \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u0645\u0646 \u0647\u0630\u0647 \u0627\u0644\u0645\u0646\u0638\u0645\u0627\u062A \u0625\u0630\u0627 \u0642\u0631\u0631\u0646\u0627 \u0645\u0627 \u064A\u0644\u064A: ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(94, "ul", 14);
    i0.ɵɵelementStart(95, "li");
    i0.ɵɵtext(96, "\u0644\u0646 \u064A\u062C\u0639\u0644\u0646\u0627 \u0627\u0644\u0631\u0627\u0628\u0637 \u0646\u0646\u0638\u0631 \u0625\u0644\u0649 \u0623\u0646\u0641\u0633\u0646\u0627 \u0623\u0648 \u0625\u0644\u0649 \u0623\u0639\u0645\u0627\u0644\u0646\u0627 \u0627\u0644\u0645\u0639\u062A\u0645\u062F\u0629 \u0628\u0634\u0643\u0644 \u0633\u0644\u0628\u064A");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(97, "li");
    i0.ɵɵtext(98, "\u0644\u064A\u0633 \u0644\u062F\u0649 \u0627\u0644\u0645\u0646\u0638\u0645\u0629 \u0623\u064A \u0633\u062C\u0644\u0627\u062A \u0633\u0644\u0628\u064A\u0629 \u0645\u0639\u0646\u0627");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(99, "li");
    i0.ɵɵtext(100, "\u062A\u0639\u0648\u0636 \u0627\u0644\u0641\u0627\u0626\u062F\u0629 \u0627\u0644\u062A\u064A \u062A\u0639\u0648\u062F \u0639\u0644\u064A\u0646\u0627 \u0645\u0646 \u0631\u0624\u064A\u0629 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u0627\u0644\u062A\u0634\u0639\u0628\u064A \u063A\u064A\u0627\u0628");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(101, "li");
    i0.ɵɵtext(102, "\u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u0641\u064A \u0633\u064A\u0627\u0642 \u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0627\u0644\u0645\u0648\u0627\u0631\u062F \u0627\u0644\u0639\u0627\u0645\u0629");
    i0.ɵɵelementEnd();
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(103, "p");
    i0.ɵɵtext(104, " \u0642\u062F \u062A\u0631\u062A\u0628\u0637 \u0647\u0630\u0647 \u0627\u0644\u0645\u0646\u0638\u0645\u0627\u062A \u0628\u0635\u0641\u062D\u062A\u0646\u0627 \u0627\u0644\u0631\u0626\u064A\u0633\u064A\u0629 \u0637\u0627\u0644\u0645\u0627 \u0623\u0646 \u0627\u0644\u0631\u0627\u0628\u0637:");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(105, "ul", 14);
    i0.ɵɵelementStart(106, "li");
    i0.ɵɵtext(107, "\u0644\u064A\u0633 \u0645\u062E\u0627\u062F\u0639\u064B\u0627 \u0628\u0623\u064A \u0634\u0643\u0644 \u0645\u0646 \u0627\u0644\u0623\u0634\u0643\u0627\u0644");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(108, "li");
    i0.ɵɵtext(109, "\u0644\u0627 \u064A\u0639\u0646\u064A \u0636\u0645\u0646\u064A\u064B\u0627 \u0631\u0639\u0627\u064A\u0629 \u0623\u0648 \u062A\u0623\u064A\u064A\u062F \u0623\u0648 \u0645\u0648\u0627\u0641\u0642\u0629 \u0627\u0644\u0637\u0631\u0641 \u0627\u0644\u0645\u0631\u062A\u0628\u0637 \u0648\u0645\u0646\u062A\u062C\u0627\u062A\u0647 \u0623\u0648 \u062E\u062F\u0645\u0627\u062A\u0647");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(110, "li");
    i0.ɵɵtext(111, "\u064A\u0646\u0627\u0633\u0628 \u0633\u064A\u0627\u0642 \u0645\u0648\u0642\u0639 \u0627\u0644\u0637\u0631\u0641 \u0627\u0644\u0645\u0631\u062A\u0628\u0637");
    i0.ɵɵelementEnd();
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(112, "p");
    i0.ɵɵtext(113, " \u0625\u0630\u0627 \u0643\u0646\u062A \u0623\u062D\u062F \u0627\u0644\u0645\u0646\u0638\u0645\u0627\u062A \u0627\u0644\u0645\u062F\u0631\u062C\u0629 \u0641\u064A \u0627\u0644\u0641\u0642\u0631\u0629 2 \u0623\u0639\u0644\u0627\u0647 \u0648\u062A\u0647\u062A\u0645 \u0628\u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u0628\u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0644\u0649 \u0627\u0644\u0648\u064A\u0628\u060C \u0641\u064A\u062C\u0628 \u0639\u0644\u064A\u0643 \u0625\u0628\u0644\u0627\u063A\u0646\u0627 \u0639\u0646 \u0637\u0631\u064A\u0642 \u0625\u0631\u0633\u0627\u0644 \u0628\u0631\u064A\u062F \u0625\u0644\u0643\u062A\u0631\u0648\u0646\u064A \u0625\u0644\u0649 \u0634\u0631\u0643\u062A\u0646\u0627. \u064A\u0631\u062C\u0649 \u062A\u0636\u0645\u064A\u0646 \u0627\u0633\u0645\u0643 \u0648\u0627\u0633\u0645 \u0645\u0624\u0633\u0633\u062A\u0643 \u0648\u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0627\u0644\u0627\u062A\u0635\u0627\u0644 \u0628\u0627\u0644\u0625\u0636\u0627\u0641\u0629 \u0625\u0644\u0649 \u0639\u0646\u0648\u0627\u0646 URL \u0644\u0645\u0648\u0642\u0639\u0643 \u0648\u0642\u0627\u0626\u0645\u0629 \u0628\u0623\u064A \u0639\u0646\u0627\u0648\u064A\u0646 URL \u062A\u0646\u0648\u064A \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u0628\u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0644\u0649 \u0627\u0644\u0648\u064A\u0628 \u0648\u0642\u0627\u0626\u0645\u0629 \u0639\u0646\u0627\u0648\u064A\u0646 URL \u0627\u0644\u0645\u0648\u062C\u0648\u062F\u0629 \u0639\u0644\u0649 \u0645\u0648\u0642\u0639\u0646\u0627 \u0648\u0627\u0644\u062A\u064A \u062A\u0631\u063A\u0628 \u0641\u064A \u0627\u0644\u0648\u0635\u0648\u0644 \u0625\u0644\u064A\u0647\u0627 \u062D\u0644\u0642\u0629 \u0627\u0644\u0648\u0635\u0644. \u0639\u0644\u064A\u0643 \u0627\u0644\u0627\u0646\u062A\u0638\u0627\u0631 2-3 \u0627\u0633\u0627\u0628\u064A\u0639 \u0644\u064A\u0635\u0644\u0643 \u0627\u0644\u0631\u062F \u0645\u0646 \u0627\u0644\u0641\u0631\u064A\u0642. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(114, "p");
    i0.ɵɵtext(115, " \u064A\u062C\u0648\u0632 \u0644\u0644\u0645\u0646\u0638\u0645\u0627\u062A \u0627\u0644\u0645\u0639\u062A\u0645\u062F\u0629 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u0627\u0644\u062A\u0634\u0639\u0628\u064A \u0644\u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0644\u0649 \u0627\u0644\u0646\u062D\u0648 \u0627\u0644\u062A\u0627\u0644\u064A: ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(116, "ul", 14);
    i0.ɵɵelementStart(117, "li");
    i0.ɵɵtext(118, "\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0627\u0633\u0645 \u0634\u0631\u0643\u062A\u0646\u0627");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(119, "li");
    i0.ɵɵtext(120, "\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0645\u062D\u062F\u062F \u0645\u0648\u0642\u0639 \u0627\u0644\u0645\u0648\u0627\u0631\u062F \u0627\u0644\u0645\u0648\u062D\u062F \u0627\u0644\u0630\u064A \u064A\u062A\u0645 \u0631\u0628\u0637\u0647");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(121, "li");
    i0.ɵɵtext(122, "\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0623\u064A \u0648\u0635\u0641 \u0622\u062E\u0631 \u0644\u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0644\u0649 \u0627\u0644\u0648\u064A\u0628 \u064A\u062A\u0645 \u0631\u0628\u0637\u0647 \u0628\u0647\u0630\u0627 \u0627\u0644\u0623\u0645\u0631 \u0641\u064A \u0633\u064A\u0627\u0642 \u0648\u0634\u0643\u0644 \u0627\u0644\u0645\u062D\u062A\u0648\u0649 \u0639\u0644\u0649 \u0645\u0648\u0642\u0639 \u0627\u0644\u0637\u0631\u0641 \u0627\u0644\u0645\u0631\u062A\u0628\u0637");
    i0.ɵɵelementEnd();
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(123, "p");
    i0.ɵɵtext(124, "\u0644\u0646 \u064A\u064F\u0633\u0645\u062D \u0628\u0623\u064A \u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0644\u0634\u0639\u0627\u0631 \u0634\u0631\u0643\u062A\u0646\u0627 \u0623\u0648 \u0623\u064A \u0639\u0645\u0644 \u0641\u0646\u064A \u0622\u062E\u0631 \u0644\u0631\u0628\u0637 \u063A\u064A\u0627\u0628 \u0627\u062A\u0641\u0627\u0642\u064A\u0629 \u062A\u0631\u062E\u064A\u0635 \u0627\u0644\u0639\u0644\u0627\u0645\u0629 \u0627\u0644\u062A\u062C\u0627\u0631\u064A\u0629.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(125, "h3");
    i0.ɵɵtext(126, "\u0625\u0637\u0627\u0631\u0627\u062A");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(127, "p");
    i0.ɵɵtext(128, " \u0628\u062F\u0648\u0646 \u0645\u0648\u0627\u0641\u0642\u0629 \u0645\u0633\u0628\u0642\u0629 \u0648\u0625\u0630\u0646 \u0643\u062A\u0627\u0628\u064A\u060C \u0644\u0627 \u064A\u062C\u0648\u0632 \u0644\u0643 \u0625\u0646\u0634\u0627\u0621 \u0625\u0637\u0627\u0631\u0627\u062A \u062D\u0648\u0644 \u0635\u0641\u062D\u0627\u062A \u0627\u0644\u0648\u064A\u0628 \u0627\u0644\u062E\u0627\u0635\u0629 \u0628\u0646\u0627 \u0648\u0627\u0644\u062A\u064A \u062A\u063A\u064A\u0631 \u0628\u0623\u064A \u0634\u0643\u0644 \u0645\u0646 \u0627\u0644\u0623\u0634\u0643\u0627\u0644 \u0627\u0644\u0639\u0631\u0636 \u0627\u0644\u062A\u0642\u062F\u064A\u0645\u064A \u0627\u0644\u0645\u0631\u0626\u064A \u0644\u0645\u0648\u0642\u0639\u0646\u0627 \u0623\u0648 \u0645\u0638\u0647\u0631\u0647. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(129, "h3");
    i0.ɵɵtext(130, "\u0627\u0644\u0645\u0633\u0624\u0648\u0644\u064A\u0629 \u0639\u0646 \u0627\u0644\u0645\u062D\u062A\u0648\u0649");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(131, "p");
    i0.ɵɵtext(132, " \u0644\u0646 \u0646\u062A\u062D\u0645\u0644 \u0627\u0644\u0645\u0633\u0624\u0648\u0644\u064A\u0629 \u0639\u0646 \u0623\u064A \u0645\u062D\u062A\u0648\u0649 \u064A\u0638\u0647\u0631 \u0639\u0644\u0649 \u0645\u0648\u0642\u0639 \u0627\u0644\u0648\u064A\u0628 \u0627\u0644\u062E\u0627\u0635 \u0628\u0643. \u0623\u0646\u062A \u062A\u0648\u0627\u0641\u0642 \u0639\u0644\u0649 \u062D\u0645\u0627\u064A\u062A\u0646\u0627 \u0648\u0627\u0644\u062F\u0641\u0627\u0639 \u0639\u0646\u0627 \u0636\u062F \u062C\u0645\u064A\u0639 \u0627\u0644\u0645\u0637\u0627\u0644\u0628\u0627\u062A \u0627\u0644\u062A\u064A \u062A\u0646\u0634\u0623 \u0639\u0644\u0649 \u0645\u0648\u0642\u0639 \u0627\u0644\u0648\u064A\u0628 \u0627\u0644\u062E\u0627\u0635 \u0628\u0643. \u064A\u062C\u0628 \u0623\u0644\u0627 \u064A\u0638\u0647\u0631 \u0623\u064A \u0631\u0627\u0628\u0637 (\u0631\u0648\u0627\u0628\u0637) \u0639\u0644\u0649 \u0623\u064A \u0645\u0648\u0642\u0639 \u0648\u064A\u0628 \u064A\u0645\u0643\u0646 \u062A\u0641\u0633\u064A\u0631\u0647 \u0639\u0644\u0649 \u0623\u0646\u0647 \u062A\u0634\u0647\u064A\u0631\u064A \u0623\u0648 \u0641\u0627\u062D\u0634 \u0623\u0648 \u0625\u062C\u0631\u0627\u0645\u064A\u060C \u0623\u0648 \u064A\u0646\u062A\u0647\u0643 \u0623\u0648 \u064A\u062F\u0639\u0648 \u0625\u0644\u0649 \u0627\u0644\u062A\u0639\u062F\u064A \u0623\u0648 \u0627\u0644\u0627\u0646\u062A\u0647\u0627\u0643 \u0627\u0644\u0622\u062E\u0631 \u0644\u062D\u0642\u0648\u0642 \u0623\u064A \u0637\u0631\u0641 \u062B\u0627\u0644\u062B. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(133, "h3");
    i0.ɵɵtext(134, "\u062A\u062D\u0641\u0638 \u0627\u0644\u062D\u0642\u0648\u0642");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(135, "p");
    i0.ɵɵtext(136, " \u0646\u062D\u062A\u0641\u0638 \u0628\u0627\u0644\u062D\u0642 \u0641\u064A \u0645\u0637\u0627\u0644\u0628\u062A\u0643 \u0628\u0625\u0632\u0627\u0644\u0629 \u062C\u0645\u064A\u0639 \u0627\u0644\u0631\u0648\u0627\u0628\u0637 \u0623\u0648 \u0623\u064A \u0631\u0627\u0628\u0637 \u0645\u0639\u064A\u0646 \u0644\u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0644\u0649 \u0627\u0644\u0648\u064A\u0628. \u0623\u0646\u062A \u062A\u0648\u0627\u0641\u0642 \u0639\u0644\u0649 \u0625\u0632\u0627\u0644\u0629 \u062C\u0645\u064A\u0639 \u0627\u0644\u0631\u0648\u0627\u0628\u0637 \u0625\u0644\u0649 \u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0644\u0649 \u0627\u0644\u0641\u0648\u0631 \u0639\u0646\u062F \u0627\u0644\u0637\u0644\u0628. \u0646\u062D\u062A\u0641\u0638 \u0623\u064A\u0636\u064B\u0627 \u0628\u0627\u0644\u062D\u0642 \u0641\u064A \u062A\u0639\u062F\u064A\u0644 \u0647\u0630\u0647 \u0627\u0644\u0634\u0631\u0648\u0637 \u0648\u0627\u0644\u0623\u062D\u0643\u0627\u0645 \u0648\u0633\u064A\u0627\u0633\u0629 \u0627\u0644\u0631\u0628\u0637 \u0627\u0644\u062E\u0627\u0635\u0629 \u0628\u0647\u0627 \u0641\u064A \u0623\u064A \u0648\u0642\u062A. \u0645\u0646 \u062E\u0644\u0627\u0644 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u0627\u0644\u0645\u0633\u062A\u0645\u0631 \u0628\u0645\u0648\u0642\u0639\u0646\u0627 \u060C \u0641\u0625\u0646\u0643 \u062A\u0648\u0627\u0641\u0642 \u0639\u0644\u0649 \u0627\u0644\u0627\u0644\u062A\u0632\u0627\u0645 \u0628\u0634\u0631\u0648\u0637 \u0648\u0623\u062D\u0643\u0627\u0645 \u0627\u0644\u0631\u0628\u0637 \u0647\u0630\u0647 \u0648\u0627\u062A\u0628\u0627\u0639\u0647\u0627. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(137, "h3");
    i0.ɵɵtext(138, "\u0625\u0632\u0627\u0644\u0629 \u0627\u0644\u0631\u0648\u0627\u0628\u0637 \u0645\u0646 \u0645\u0648\u0642\u0639\u0646\u0627");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(139, "p");
    i0.ɵɵtext(140, " \u0625\u0630\u0627 \u0648\u062C\u062F\u062A \u0623\u064A \u0631\u0627\u0628\u0637 \u0639\u0644\u0649 \u0645\u0648\u0642\u0639\u0646\u0627 \u0645\u0633\u064A\u0621 \u0644\u0623\u064A \u0633\u0628\u0628 \u0645\u0646 \u0627\u0644\u0623\u0633\u0628\u0627\u0628\u060C \u0641\u0623\u0646\u062A \u062D\u0631 \u0641\u064A \u0627\u0644\u0627\u062A\u0635\u0627\u0644 \u0648\u0625\u0628\u0644\u0627\u063A\u0646\u0627 \u0641\u064A \u0623\u064A \u0644\u062D\u0638\u0629. \u0633\u0646\u0646\u0638\u0631 \u0641\u064A \u0637\u0644\u0628\u0627\u062A \u0625\u0632\u0627\u0644\u0629 \u0627\u0644\u0631\u0648\u0627\u0628\u0637\u060C \u0648\u0644\u0643\u0646\u0646\u0627 \u0644\u0633\u0646\u0627 \u0645\u0644\u0632\u0645\u064A\u0646 \u0628\u0630\u0644\u0643 \u0623\u0648 \u0628\u0627\u0644\u0631\u062F \u0639\u0644\u064A\u0643 \u0645\u0628\u0627\u0634\u0631\u0629.\n\u0644\u0627 \u0646\u0636\u0645\u0646 \u0635\u062D\u0629 \u0627\u0644\u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0627\u0644\u0648\u0627\u0631\u062F\u0629 \u0641\u064A \u0647\u0630\u0627 \u0627\u0644\u0645\u0648\u0642\u0639\u060C \u0648\u0644\u0627 \u0646\u0636\u0645\u0646 \u0627\u0643\u062A\u0645\u0627\u0644\u0647\u0627 \u0623\u0648 \u062F\u0642\u062A\u0647\u0627\u061B \u0643\u0645\u0627 \u0623\u0646\u0646\u0627 \u0644\u0627 \u0646\u0639\u062F \u0628\u0636\u0645\u0627\u0646 \u0628\u0642\u0627\u0621 \u0645\u0648\u0642\u0639 \u0627\u0644\u0648\u064A\u0628 \u0645\u062A\u0627\u062D\u064B\u0627 \u0623\u0648 \u062A\u062D\u062F\u064A\u062B \u0627\u0644\u0645\u0648\u0627\u062F \u0627\u0644\u0645\u0648\u062C\u0648\u062F\u0629 \u0639\u0644\u0649 \u0627\u0644\u0645\u0648\u0642\u0639. ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(141, "h3");
    i0.ɵɵtext(142, "\u0627\u0644\u062D\u0633\u0627\u0628\u0627\u062A ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(143, "ul", 14);
    i0.ɵɵelementStart(144, "li");
    i0.ɵɵtext(145, "\u064A\u0644\u062A\u0632\u0645 \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u0628\u0639\u062F\u0645 \u0625\u0646\u0634\u0627\u0621 \u0623\u0643\u062B\u0631 \u0645\u0646 \u062D\u0633\u0627\u0628 \u0648\u0627\u062D\u062F \u0641\u064A \u0645\u0646\u0635\u0629 \u0628\u062D\u062B\u064A\u060C \u0648\u0641\u064A \u062D\u0627\u0644 \u0648\u0627\u062C\u0647 \u0645\u0634\u0643\u0644\u0629 \u0641\u064A \u062D\u0633\u0627\u0628\u0647 \u0627\u0644\u0623\u0648\u0644 \u0641\u0639\u0644\u064A\u0647 \u0627\u0644\u0645\u062A\u0627\u0628\u0639\u0629 \u0645\u0639 \u0641\u0631\u064A\u0642 \u0628\u062D\u062B\u064A \u0644\u062D\u0644 \u0627\u0644\u0645\u0634\u0643\u0644\u0629.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(146, "li");
    i0.ɵɵtext(147, "\u064A\u062A\u0639\u0647\u062F \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u0627\u0644\u0645\u0633\u062C\u0644 \u0641\u064A \u0645\u0646\u0635\u0629 \u0628\u062D\u062B\u064A \u0623\u0646\u0647 \u0627\u0644\u0648\u062D\u064A\u062F \u0627\u0644\u0630\u064A \u064A\u0633\u062A\u062E\u062F\u0645 \u0627\u0644\u062D\u0633\u0627\u0628 \u0648\u064A\u0643\u0648\u0646 \u0645\u0633\u0624\u0648\u0644\u0627 \u0639\u0646 \u0643\u0644 \u0645\u0627 \u064A\u062A\u0645 \u0639\u0628\u0631 \u062D\u0633\u0627\u0628\u0647\u060C \u0648\u0644\u0627 \u064A\u0633\u0645\u062D \u062D\u0627\u0644\u064A\u0627 \u0623\u0646 \u064A\u0642\u0648\u0645 \u0623\u0643\u062B\u0631 \u0645\u0646 \u0634\u062E\u0635 \u0628\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u062D\u0633\u0627\u0628 \u0648\u0627\u062D\u062F.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(148, "li");
    i0.ɵɵtext(149, "\u0642\u062F \u064A\u0637\u0644\u0628 \u0645\u0648\u0642\u0639 \u0628\u062D\u062B\u064A \u0648\u062B\u0627\u0626\u0642 \u0634\u062E\u0635\u064A\u0629 \u0645\u062B\u0644 \u0627\u0644\u0647\u0648\u064A\u0629 \u0623\u0648 \u062C\u0648\u0627\u0632 \u0627\u0644\u0633\u0641\u0631 \u0644\u0625\u062B\u0628\u0627\u062A \u0647\u0648\u064A\u0629 \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u0642\u0628\u0644 \u0627\u0644\u0633\u0645\u0627\u062D \u0644\u0647 \u0628\u0625\u0643\u0645\u0627\u0644 \u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0627\u0644\u0645\u0648\u0642\u0639.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(150, "li");
    i0.ɵɵtext(151, "-\t\u0642\u062F \u064A\u0642\u0648\u0645 \u0641\u0631\u064A\u0642 \u0628\u062D\u062B\u064A \u0628\u0625\u064A\u0642\u0627\u0641 \u062D\u0633\u0627\u0628 \u0623\u064A \u0645\u0633\u062A\u062E\u062F\u0645 \u064A\u062A\u0645 \u0627\u0644\u0634\u0643 \u0641\u064A\u0647 \u0623\u0648 \u0628\u0645\u062E\u0627\u0644\u0641\u062A\u0647 \u0644\u0644\u0634\u0631\u0648\u0637\u060C \u0648\u064A\u062D\u0642 \u0644\u0644\u0645\u0648\u0642\u0639 \u0637\u0644\u0628 \u0648\u062B\u0627\u0626\u0642 \u0634\u062E\u0635\u064A\u0629 \u0639\u0646 \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u062A\u062B\u0628\u062A \u0647\u0648\u064A\u062A\u0647 \u0644\u0625\u0639\u0627\u062F\u0629 \u062A\u0641\u0639\u064A\u0644 \u0627\u0644\u062D\u0633\u0627\u0628\u060C \u0643\u0630\u0644\u0643 \u0642\u062F \u064A\u062A\u0645 \u0637\u0644\u0628 \u0648\u062B\u0627\u0626\u0642 \u0644\u0644\u062A\u0623\u0643\u062F \u0645\u0646 \u0645\u0644\u0643\u064A\u062A\u0647 \u0644\u062D\u0633\u0627\u0628 \u0628\u0627\u064A \u0628\u0627\u0644 \u0623\u0648 \u0627\u0644\u0628\u0637\u0627\u0642\u0629 \u0627\u0644\u0627\u0626\u062A\u0645\u0627\u0646\u064A\u0629.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(152, "li");
    i0.ɵɵtext(153, "\u064A\u062D\u0642 \u0644\u0645\u0648\u0642\u0639 \u0628\u062D\u062B\u064A \u062D\u0638\u0631 \u0627\u0644\u062D\u0633\u0627\u0628\u0627\u062A \u0627\u0644\u0645\u062E\u0627\u0644\u0641\u0629 \u0644\u0634\u0631\u0648\u0637 \u0627\u0644\u0645\u0648\u0642\u0639 \u0623\u0648 \u062A\u0644\u0643 \u0627\u0644\u062A\u064A \u062A\u062A\u0633\u0628\u0628 \u0628\u0636\u0631\u0631 \u0644\u0644\u0645\u0648\u0642\u0639 \u0623\u0648 \u0645\u0633\u062A\u062E\u062F\u0645\u064A\u0647 \u0628\u0634\u0643\u0644 \u0646\u0647\u0627\u0626\u064A \u0648\u062F\u0648\u0646 \u0631\u062C\u0648\u0639 \u0644\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u0623\u0648 \u062A\u0646\u0628\u064A\u0647.");
    i0.ɵɵelementEnd();
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(154, "p");
    i0.ɵɵtext(155, " \u064A\u062A\u0645 \u062A\u062C\u0645\u064A\u062F \u0627\u0644\u062D\u0633\u0627\u0628 \u0645\u0624\u0642\u062A\u0627\u064B \u0623\u0648 \u0625\u064A\u0642\u0627\u0641\u0647 \u0628\u0634\u0643\u0644 \u062F\u0627\u0626\u0645 \u0641\u064A \u0645\u0648\u0642\u0639\u0646\u0627 \u0639\u0646\u062F \u0627\u0646\u062A\u0647\u0627\u0643 \u0634\u0631\u0648\u0637 \u0627\u0644\u0627\u0633\u062A\u062E\u062F\u0627\u0645. \u0648\u064A\u062A\u0644\u0642\u0649 \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u062A\u0646\u0628\u064A\u0647\u0627\u064B \u0628\u0630\u0644\u0643 \u0639\u0628\u0631 \u0627\u0644\u0645\u0648\u0642\u0639 \u0623\u0648 \u0627\u0644\u0628\u0631\u064A\u062F \u0627\u0644\u0625\u0644\u0643\u062A\u0631\u0648\u0646\u064A. \u0645\u0646 \u0627\u0644\u0623\u0645\u062B\u0644\u0629 \u0639\u0644\u0649 \u0627\u0644\u0627\u0633\u062A\u062E\u062F\u0627\u0645\u0627\u062A \u063A\u064A\u0631 \u0627\u0644\u0642\u0627\u0646\u0648\u0646\u064A\u0629 \u0627\u0644\u062A\u064A \u062A\u0639\u0631\u0636 \u0627\u0644\u062D\u0633\u0627\u0628 \u0644\u0644\u062A\u062C\u0645\u064A\u062F \u0623\u0648 \u0627\u0644\u0625\u064A\u0642\u0627\u0641: ");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(156, "ul", 14);
    i0.ɵɵelementStart(157, "li");
    i0.ɵɵtext(158, "\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u0648\u0633\u064A\u0644\u0629 \u062F\u0641\u0639 \u0645\u0633\u0631\u0648\u0642\u0629 \u0623\u0648 \u063A\u064A\u0631 \u0645\u0645\u0644\u0648\u0643\u0629 \u0645\u0646 \u0642\u0628\u0644\u0647");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(159, "li");
    i0.ɵɵtext(160, "\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0627\u0644\u062D\u0633\u0627\u0628 \u0645\u0646 \u0642\u0628\u0644 \u0623\u0643\u062B\u0631 \u0645\u0646 \u0634\u062E\u0635 \u0633\u0648\u0627\u0621 \u0643\u0627\u0646\u0648\u0627 \u0641\u0631\u064A\u0642\u0627 \u0623\u0645 \u0634\u0631\u0643\u0629\u060C \u062D\u064A\u062B \u064A\u0633\u0645\u062D \u0641\u0642\u0637 \u0644\u0634\u062E\u0635 \u0648\u0627\u062D\u062F \u0628\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0627\u0644\u062D\u0633\u0627\u0628 \u0641\u064A \u0645\u0633\u062A\u0642\u0644 \u0648\u0627\u0644\u0639\u0645\u0644 \u0639\u0644\u0649 \u0627\u0644\u0645\u0634\u0627\u0631\u064A\u0639 \u0641\u064A\u0647");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(161, "li");
    i0.ɵɵtext(162, "\u0642\u064A\u0627\u0645 \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 (\u0627\u0644\u062E\u0628\u064A\u0631) \u0628\u0644\u0639\u0628 \u062F\u0648\u0631 \u0627\u0644\u0648\u0633\u064A\u0637 \u0641\u064A \u0627\u0644\u0645\u0634\u0627\u0631\u064A\u0639\u060C \u0628\u062D\u064A\u062B \u064A\u0633\u062A\u0644\u0645 \u0645\u0634\u0631\u0648\u0639\u0627 \u0643\u062E\u0628\u064A\u0631 \u062B\u0645 \u064A\u0642\u0648\u0645 \u0628\u062A\u0648\u0638\u064A\u0641 \u0634\u062E\u0635 \u0622\u062E\u0631 \u0644\u0644\u0639\u0645\u0644 \u0639\u0644\u064A\u0647 \u062E\u0627\u0631\u062C \u0623\u0648 \u062F\u0627\u062E\u0644 \u0645\u0648\u0642\u0639 \u0628\u062D\u062B\u064A (\u0628\u0647\u0630\u0647 \u0627\u0644\u062D\u0627\u0644\u0629 \u064A\u062A\u0645 \u0625\u064A\u0642\u0627\u0641 \u0627\u0644\u0648\u0633\u064A\u0637 \u0648\u0625\u0644\u0632\u0627\u0645\u0647 \u0628\u062F\u0641\u0639 \u0623\u064A \u0645\u0628\u0627\u0644\u063A \u0645\u0633\u062A\u062D\u0642\u0629 \u0648\u0627\u0644\u0642\u064A\u0627\u0645 \u0628\u0623\u064A \u0623\u0639\u0645\u0627\u0644 \u062D\u0633\u0628 \u062A\u0642\u062F\u064A\u0631 \u0627\u0644\u062F\u0639\u0645 \u0627\u0644\u0641\u0646\u064A)");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(163, "li");
    i0.ɵɵtext(164, "\u0645\u062D\u0627\u0648\u0644\u0629 \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u0627\u0644\u0627\u062D\u062A\u064A\u0627\u0644 \u0639\u0644\u0649 \u0645\u0648\u0642\u0639 \u0628\u062D\u062B\u064A \u0628\u0637\u0644\u0628 \u0643\u0627\u0645\u0644 \u0627\u0644\u0639\u0645\u0644 \u0641\u064A \u0627\u0644\u0631\u0633\u0627\u0626\u0644 \u0642\u0628\u0644 \u062A\u0648\u0638\u064A\u0641\u0647");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(165, "li");
    i0.ɵɵtext(166, "\u062A\u0633\u0628\u0628 \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u0628\u0623\u064A \u0636\u0631\u0631 \u062A\u062C\u0627\u0647 \u0623\u064A \u0645\u0633\u062A\u062E\u062F\u0645 \u0622\u062E\u0631 \u0623\u0648 \u0645\u0648\u0642\u0639 \u0628\u062D\u062B\u064A");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(167, "li");
    i0.ɵɵtext(168, "\u062A\u0647\u0631\u0628 \u0627\u0644\u062E\u0628\u064A\u0631 \u0645\u0646 \u062F\u0641\u0639 \u0639\u0645\u0648\u0644\u0629 \u0627\u0644\u0645\u0646\u0635\u0629 \u0639\u0628\u0631 \u0627\u0644\u062F\u0641\u0639 \u0627\u0644\u062E\u0627\u0631\u062C\u064A \u0648\u064A\u0643\u0648\u0646 \u0627\u0644\u062D\u0638\u0631 \u0646\u0647\u0627\u0626\u064A\u0627 \u0645\u0628\u0627\u0634\u0631\u0629");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(169, "li");
    i0.ɵɵtext(170, "\u0627\u0644\u0625\u0635\u0631\u0627\u0631 \u0639\u0644\u0649 \u0627\u0644\u062A\u0648\u0627\u0635\u0644 \u062E\u0627\u0631\u062C \u0645\u0646\u0635\u0629 \u0645\u0633\u062A\u0642\u0644 \u0628\u062F\u0648\u0646 \u0636\u0631\u0648\u0631\u0629");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(171, "li");
    i0.ɵɵtext(172, "\u0639\u062F\u0645 \u0645\u062A\u0627\u0628\u0639\u0629 \u0627\u0644\u062E\u0628\u064A\u0631 \u0644\u0644\u0639\u0645\u0644 \u0628\u0639\u062F \u0627\u0644\u0628\u062F\u0621 \u0641\u064A\u0647 \u0628\u062F\u0648\u0646 \u0633\u0628\u0628 \u062C\u0648\u0647\u0631\u064A \u0648\u0627\u0644\u062A\u0633\u0628\u0628 \u0628\u0625\u0636\u0627\u0639\u0629 \u0648\u0642\u062A \u0627\u0644\u0628\u0627\u062D\u062B");
    i0.ɵɵelementEnd();
    i0.ɵɵelementStart(173, "li");
    i0.ɵɵtext(174, "\u0642\u064A\u0627\u0645 \u0627\u0644\u062E\u0628\u064A\u0631 \u0628\u0646\u0634\u0631\u0639\u0645\u0644 \u0644\u064A\u0633 \u0645\u0646 \u0625\u0639\u062F\u0627\u062F\u0647 \u0641\u064A \u0645\u0639\u0631\u0636 \u0623\u0639\u0645\u0627\u0644\u0647");
